const int MAX_T = 1e5;

class Solution {
 public:
  static int minimumAddedCoins(vi& coins, int target) {
    SORT(coins);
    auto it = coins.begin();
    int cnt = 0;
    int x = 0;
    while (x < target) {
      if (it == coins.end()) {
        ++cnt;
        x = x + (x + 1);
      } else if (*it <= x+1) {
        x = std::max(x + *it, x);
        ++it;
      } else { // *it > x+1
        ++cnt;
        x = x + (x + 1);
      }
    }
    
    return cnt;
  }
};

